package java_Learning;

public class DataTypeDemo {
	
	// Primitive and Non-Primitive
	//primitive- boolean,int
	//datatype  variablename=value;

	public static void main(String[] args) {
		// TO DO Auto-generated method stub
		
		
		//boolean
		
		boolean flag=false;
		System.out.println("boolean:-"+flag);
		
		//integer
           
		byte b=100;  // -128 to +127
		short s=150;  // -32768 to 32767
		int k=20132132;  // -2,147,483,648 to 2,147,483,647 
		long l=92233720368547758l;  // -9,223,372,036,854,775,808 to 9,223,372,036,854,775,807
		
		System.out.println("byte:-"+b);
		System.out.println("short:-"+s);
		System.out.println("int:-"+k);
		System.out.println("long:-"+l);
		
		
		//floating-point
		
		float f=100.245645463456f;  // 6 decimal
		double d=1000.224654654465d;  // 15 decimal
		System.out.println("float:-"+f);
		System.out.println("double number:-"+d);
		
		
		//character
		
		char cha='f';
		System.out.println("character:-"+cha);
		
				

	}

}


